mapper.select:
SELECT b.id, p.patient_number AS patientNumber, p.patient_name AS patientName, p.hospital_number AS hospitalNumber, dt.disease_type AS diseaseType, p.diagnose, b.eeg_record AS eegRecord, b.eeg_detail AS eegDetail, b.eeg_type AS eegType, b.have_sleep AS haveSleep, b.apply_doctor AS applyDoctor, DATE_FORMAT(b.gather_date, '%Y-%m-%d') AS gatherDate, DATE_FORMAT(b.create_date, '%Y-%m-%d') AS createDate, b.creater, DATE_FORMAT(b.update_date, '%Y-%m-%d') AS updateDate, b.updater, b.is_deleted AS isDeleted, b.status FROM eeg b LEFT JOIN patient_record p ON b.patient_number = p.id LEFT JOIN disease_type dt ON p.disease_type= dt.id
AND p.patient_gender = #{patientGender}
AND p.disease_type = #{diseaseType}
AND p.diagnose LIKE '%' #{diagnose} '%'
AND DATE_FORMAT(b.gather_date, '%Y-%m-%d') = #{gatherDate}
AND b.eeg_detail LIKE '%' #{eegDetail} '%'
AND b.have_sleep = #{haveSleep}
AND p.birthday = #{birthday}
AND p.dimensions = #{dimensions}
AND (p.patient_number LIKE '%' #{patientNumber} '%' OR p.hospital_number LIKE '%' #{patientNumber} '%')
AND b.is_deleted = #{isDeleted}
ORDER BY b.create_date DESC
LIMIT #{page}, #{rows}

		SELECT
			COUNT(0)
        FROM eeg b LEFT JOIN patient_record p ON b.patient_number = p.id


							AND p.patient_gender = #{patientGender}


							AND p.disease_type = #{diseaseType}


							AND p.diagnose LIKE '%' #{diagnose} '%'


							AND DATE_FORMAT(b.gather_date, '%Y-%m-%d') = #{gatherDate}


							AND b.eeg_detail LIKE '%' #{eegDetail} '%'


							AND b.have_sleep = #{haveSleep}


							AND p.birthday = #{birthday}


							AND p.dimensions = #{dimensions}


							AND (p.patient_number LIKE '%' #{patientNumber} '%' OR p.hospital_number LIKE '%' #{patientNumber} '%')





							AND b.is_deleted = #{isDeleted}

SELECT COUNT(0) AS cnt
FROM eeg
WHERE DATE_FORMAT(create_date, '%Y-%m-%d') < #{startDate}

SELECT COUNT(0) AS cnt, DATE_FORMAT(create_date, '%Y-%m-%d') AS createDate FROM eeg
AND eeg_detail LIKE '%' #{keyset} '%'
AND DATE_FORMAT(create_date, '%Y-%m-%d') >= #{startDate}
AND DATE_FORMAT(create_date, '%Y-%m-%d') <= #{endDate}
GROUP BY createDate

mapper.update:
UPDATE eeg
SET is_deleted = #{status}
WHERE id = #{id}
